import React from "react";


export default class MyForm extends React.Component {
    state = {
        name: "",
        date: "",
        dateNow: Date.now(),
        days: '',
        hours: '',
        minutes: '',
        seconds: '',
    };


    handleChange = event => {
        this.setState({
            name: event.target.value,
        });
    }

    handleDate = event => {
        this.setState({
            date: event.target.value,
        })
    }

    clickSubmit = event => {
        if(this.state.name === "" || this.state.date === "") {
            console.log("No value")
        } else {

            var dateFor = new Date(this.state.date).getTime();

            var birthday = dateFor - this.state.dateNow ;

            var second = 1000;
            var minute = second * 60;
            var hour = minute * 60;
            var day = hour * 24;

            this.setState({
                days: birthday / day,
                hours: birthday % hour,
                minutes: birthday % minute,
                second: birthday % second,
            })

        } 
    }


    render() {
        console.log(this.state.days);
        console.log(this.state.hours);
        console.log(this.state.minutes);
        console.log(this.state.seconds);
        return (
        <div className="bg-container">
            <div className="form-class text-white">
                <h2 className="text-white" >INFORMATION</h2>
                <div className="form-group">
                    <label>Name:</label>
                    <input className="form-control mb-3" value={this.state.name} onChange={this.handleChange} placeholder="Enter Your Name" />
                </div>
                <div className="form-group">
                    <label>Date Birth:</label>
                    <input type="date" value={this.state.date} onChange={this.handleDate} className="form-control mb-3" /> 
                </div>

                <button className="btn btn-success" onClick={this.clickSubmit}>Submit</button>
            </div>
        </div>

        ); 
    }
}
